import {ADD_NEW_CARD, REMOVE_CARD, EDIT_CARD} from './config/constants'

export const addCard = (card) => {
    return {
        type: ADD_NEW_CARD,
        payload: card
    }
}

export const removeCard = (cardId) => {
    return {
        type: REMOVE_CARD,
        payload: cardId
    }
}

//mycode=========================================================================================

export const editCard = (card,cardId) => {
    return {
        type: EDIT_CARD,
        payload: card,
        payload: cardId
    }
}

